-- Update existing tokens with simple SVG data URIs
UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'APT';

UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'USDC';

