UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'SOL';

UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'ADA';

UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'DOT';

UPDATE tokens SET logo_url = 'data:image/svg+xml;base64,' WHERE symbol = 'MATIC';

